"use strict";

var _testing = require("../testing");

describe("#info", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/activities/activity-id", {
      id: "activity-id"
    });
    const response = await _testing.API_CLIENT.activities.info({
      activityId: "activity-id"
    });
    expect(response).toEqual({
      id: "activity-id"
    });
  });
});
describe("#list", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/activities?organizationId=org-id", {
      data: {
        activities: [{
          id: "activity-id"
        }]
      }
    });
    const response = await _testing.API_CLIENT.activities.list({
      organizationId: "org-id"
    });
    expect(response).toEqual([{
      id: "activity-id"
    }]);
  });
});