"use strict";

var _testing = require("../testing");

function _objectSpread(target) { for (var i = 1; i < arguments.length; i++) { var source = arguments[i] != null ? arguments[i] : {}; var ownKeys = Object.keys(source); if (typeof Object.getOwnPropertySymbols === 'function') { ownKeys = ownKeys.concat(Object.getOwnPropertySymbols(source).filter(function (sym) { return Object.getOwnPropertyDescriptor(source, sym).enumerable; })); } ownKeys.forEach(function (key) { _defineProperty(target, key, source[key]); }); } return target; }

function _defineProperty(obj, key, value) { if (key in obj) { Object.defineProperty(obj, key, { value: value, enumerable: true, configurable: true, writable: true }); } else { obj[key] = value; } return obj; }

describe("#info", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/projects/project-id/assets/asset-id", {
      id: "asset-id"
    });
    const response = await _testing.API_CLIENT.assets.info({
      assetId: "asset-id",
      projectId: "project-id"
    });
    expect(response).toEqual({
      id: "asset-id"
    });
  });
});
let globalProcess;
describe("#raw", () => {
  beforeAll(() => {
    globalProcess = global.process;
  });
  afterAll(() => {
    global.process = globalProcess;
  });
  test("api", async () => {
    (0, _testing.mockAPI)("/projects/project-id/assets/asset-id", {
      url: "http://apiUrl/asset"
    });
    (0, _testing.mockAPI)("/asset", {
      id: "asset-id"
    });
    const response = await _testing.API_CLIENT.assets.raw({
      assetId: "asset-id",
      projectId: "project-id"
    }, {
      disableWrite: true
    });
    expect(response).toBeInstanceOf(ArrayBuffer);
  });
  test("api - browser", async () => {
    global.process = _objectSpread({}, global.process, {
      versions: undefined
    });
    (0, _testing.mockAPI)("/projects/project-id/assets/asset-id", {
      url: "http://apiUrl/asset"
    });
    (0, _testing.mockAPI)("/asset", {
      id: "asset-id"
    });
    const response = await _testing.API_CLIENT.assets.raw({
      assetId: "asset-id",
      projectId: "project-id"
    });
    expect(response).toBeInstanceOf(ArrayBuffer);
  });
});
describe("#list", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/projects/project-id/assets?sha=sha", {
      data: {
        assets: [{
          id: "asset-id"
        }]
      }
    });
    const response = await _testing.API_CLIENT.assets.list({
      branchId: "branch-id",
      projectId: "project-id",
      sha: "sha"
    });
    expect(response).toEqual([{
      id: "asset-id"
    }]);
  });
});