"use strict";

var _testing = require("../testing");

describe("#info", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/projects/project-id/branches/branch-id/commits/sha/changeset", {
      changeset: {
        id: "changeset-id"
      }
    });
    const response = await _testing.API_CLIENT.changesets.info({
      branchId: "branch-id",
      projectId: "project-id",
      sha: "sha"
    });
    expect(response).toEqual({
      id: "changeset-id"
    });
  });
  test("cli", async () => {
    (0, _testing.mockCLI)(["changeset", "project-id", "--commit", "sha", "--branch", "branch-id"], {
      id: "changeset-id"
    });
    const response = await _testing.CLI_CLIENT.changesets.info({
      branchId: "branch-id",
      projectId: "project-id",
      sha: "sha"
    });
    expect(response).toEqual({
      id: "changeset-id"
    });
  });
});