"use strict";

var _testing = require("../testing");

describe("#info", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/projects/project-id/branches/branch-id/commits/sha/files/file-id/layers/layer-id/data", {
      layerId: "layer-id"
    });
    const response = await _testing.API_CLIENT.data.info({
      branchId: "branch-id",
      fileId: "file-id",
      layerId: "layer-id",
      projectId: "project-id",
      sha: "sha"
    });
    expect(response).toEqual({
      layerId: "layer-id"
    });
  });
  test("cli", async () => {
    (0, _testing.mockCLI)(["layer", "data", "project-id", "sha", "file-id", "layer-id"], {
      layerId: "layer-id"
    });
    const response = await _testing.CLI_CLIENT.data.info({
      branchId: "branch-id",
      fileId: "file-id",
      layerId: "layer-id",
      projectId: "project-id",
      sha: "sha"
    });
    expect(response).toEqual({
      layerId: "layer-id"
    });
  });
});