"use strict";

var _testing = require("../testing");

describe("#info", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/notifications/notification-id", {
      id: "notification-id"
    });
    const response = await _testing.API_CLIENT.notifications.info({
      notificationId: "notification-id"
    });
    expect(response).toEqual({
      id: "notification-id"
    });
  });
});
describe("#list", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/notifications?organizationId=org-id", {
      data: [{
        id: "notification-id"
      }]
    });
    const response = await _testing.API_CLIENT.notifications.list({
      organizationId: "org-id"
    });
    expect(response).toEqual([{
      id: "notification-id"
    }]);
  });
});