"use strict";

var _testing = require("../testing");

describe("#info", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/organizations/org-id", {
      data: {
        id: "org-id"
      }
    });
    const response = await _testing.API_CLIENT.organizations.info({
      organizationId: "org-id"
    });
    expect(response).toEqual({
      id: "org-id"
    });
  });
});
describe("#list", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/organizations", {
      data: [{
        id: "org-id"
      }]
    });
    const response = await _testing.API_CLIENT.organizations.list();
    expect(response).toEqual([{
      id: "org-id"
    }]);
  });
});