"use strict";

var _testing = require("../testing");

describe("#info", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/projects/project-id", {
      data: {
        id: "project-id"
      }
    });
    const response = await _testing.API_CLIENT.projects.info({
      projectId: "project-id"
    });
    expect(response).toEqual({
      id: "project-id"
    });
  });
});
describe("#list", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/projects?filter=active&organizationId=org-id", {
      data: [{
        id: "project-id"
      }]
    });
    const response = await _testing.API_CLIENT.projects.list({
      organizationId: "org-id"
    }, {
      filter: "active"
    });
    expect(response).toEqual([{
      id: "project-id"
    }]);
  });
});