"use strict";

var _testing = require("../testing");

describe("#create", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/share_links", {
      id: "share-id"
    }, 201, "post");
    const response = await _testing.API_CLIENT.shares.create({
      organizationId: "org"
    }, {
      kind: "project",
      organizationId: "org-id",
      projectId: "project-id"
    });
    expect(response).toEqual({
      id: "share-id"
    });
  });
});
describe("#info", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/share_links/share-id", {
      id: "share-id"
    });
    const response = await _testing.API_CLIENT.shares.info({
      shareId: "share-id"
    });
    expect(response).toEqual({
      id: "share-id"
    });
  });
});