"use strict";

var _testing = require("../testing");

describe("#info", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/users/user-id", {
      id: "user-id"
    });
    const response = await _testing.API_CLIENT.users.info({
      userId: "user-id"
    });
    expect(response).toEqual({
      id: "user-id"
    });
  });
});
describe("#list", () => {
  test("api", async () => {
    (0, _testing.mockAPI)("/projects/project-id/memberships", {
      data: [{
        user: {
          id: "user-id"
        }
      }]
    });
    const response = await _testing.API_CLIENT.users.list({
      projectId: "project-id"
    });
    expect(response).toEqual([{
      id: "user-id"
    }]);
  });
  test("api - organization", async () => {
    (0, _testing.mockAPI)("/organizations/organization-id/memberships", {
      data: [{
        user: {
          id: "user-id"
        }
      }]
    });
    const response = await _testing.API_CLIENT.users.list({
      organizationId: "organization-id"
    });
    expect(response).toEqual([{
      id: "user-id"
    }]);
  });
});